x = [0, 1, 2, 3, 4]
y = [0, 2, 4, 6, 8]
plt.plot(x, y, label= 'Andamento', marker= '.', markersize= 15, markerfacecolor= 'blue', markeredgecolor= 'blue', alpha= 1, color= 'red', linewidth= 2) 
#codice base per plottare un grafico linea, dopo aver impostato i valori 'x' ed 'y' del grafico.
plt.plot(x, y, 'r.-', label= 'Andamento', markersize= 25, markerfacecolor= 'r', markeredgecolor= 'r', alpha= 1, linewidth= 2) 
#stesso codice abbreviato con il colore, il marker ed il tipo di linea 'r.-' rosso, marker punto, linea contua.

#XXX LINE NUMBER TWO XXX

x2 = np.arange(0, 4.5, 0.5) 
plt.plot(x2, x2**2, 'bs-', label= 'Guadagno', markersize= 10, markerfacecolor= 'b', markeredgecolor= 'b', alpha= 1, linewidth= 3) 

plt.legend(loc= "upper left")
